INSERT INTO `sys_oper_log` VALUES (758, '发布文章', 1, 'com.ruoyi.essay.controller.EssayController.add()', 'POST', 1, 'admin', NULL, '/essay/essay', '192.168.0.106', '内网IP', '{\"createTime\":\"2023-05-05T00:28:12.623+08:00\",\"essayContent\":\"<p>网站首页 &gt;&gt; 综合新闻 &gt;&gt; 正文</p><p class=\\\"ql-align-center\\\">我校学子在全国大学生奥林匹克数学竞赛中获3项金奖</p><p class=\\\"ql-align-center\\\">时间：2023-04-28&nbsp;&nbsp;&nbsp;点击量：86</p><p>\\t\\t近日，由国际（澳门）学术研究院数学科学研究所与数学建模研究与应用期刊社联合举办第二届全国大学生奥林匹克数学竞赛落下帷幕。我校人工智能学院2022级计算机科学与技术专业学生黄长泉、2022级物联网工程专业学生俞炜佳、2022级电子信息工程专业学生李锦城荣获数学类3项金奖。成绩为省内同类本科高校首位。</p><p>\\t据悉，该赛事面向所有中华学子，从高规格的赛事中，发现和培养更多的数学爱好者。本次赛事受到了社会各界的广泛关注与支持。本次竞赛共吸引近千所高校参与，参赛高校覆盖北京大学、西安交通大学、哈尔滨工业大学、兰州大学等多所985、211重点高校；港澳地区包括香港中文大学、澳门科技大学等。（人工智能学院 供稿）</p><p class=\\\"ql-align-center\\\">\\t<img src=\\\"http://news.ygu.edu.cn/__local/6/85/C1/5A536802603D9C31705E3EEFDEA_0919C96E_9DA09.jpg\\\" width=\\\"500\\\"></p><p class=\\\"ql-align-center\\\">\\t<img src=\\\"http://news.ygu.edu.cn/__local/F/1A/15/E015450FE7579737F608B9CD171_C46F92B1_9D632.jpg\\\" width=\\\"500\\\"></p><p class=\\\"ql-align-center\\\">\\t<img src=\\\"http://news.ygu.edu.cn/__local/3/5A/A7/45C37056291342E7E1A8205A5A8_BD995E9F_9D628.jpg\\\" width=\\\"500\\\"></p><p><br></p>\",\"essayId\":7,\"essayTitle\":\"我校学子在全国大学生奥林匹克数学竞赛中获3项金奖\",\"params\":{}}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-05-05 00:28:12');
